pragma solidity 0.5.4;
import "./TronBeast.sol";  

contract Refund_m1 {

    TronBeast public tb;

    uint256 public percent = 10;
    address public admin;
    bool public send_status = true;
 
    constructor(TronBeast _tb) public {

         tb = _tb;
        admin = msg.sender; 
    }

    struct User {
        uint256 deposit_amount;
        bool isReceived;
    }
    
    mapping(address => User) public users ;
    
    function() payable external { 
      
     }

    // function withdraw(address payable _addr) external view returns (uint256 _val) {
    //     require(users[_addr].isReceived != send_status,"Received");

    //      _val = this.depositInfo(_addr).total_deposits*percent/100;
    //     // _addr.transfer(_val);
    //     users[_addr].isReceived = true;
    //     return _val;
    // }

    function changePercent(uint256  _newPercent) public {
		require(msg.sender == admin, "Not allowed");
		percent  = _newPercent;
	} 

    function checkStatus() external view returns (bool) {
 		 return users[msg.sender].isReceived;
	}

    function changeStatus(bool _newMode) public {
		require(msg.sender == admin, "Not allowed");
		send_status  = _newMode;
	}  
   
    function depositInfo(address payable _addr) external view returns (uint256 referrals, uint256 total_deposits, uint256 total_payouts, uint256 total_structure  ) {
        return tb.userInfoTotals(_addr) ;
    }  

}
